import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  Button,
  Grid,
  Card,
  CardContent,
  CardMedia,
  CardActions,
  Chip,
  Fade,
  Slide,
  Grow,
  Paper,
  Avatar
} from '@mui/material';
import {
  AutoStories,
  TrendingUp,
  LocalShipping,
  Star,
  People,
  Favorite,
  ArrowForward
} from '@mui/icons-material';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import BookCarousel from '../components/BookCarousel';
import { featuredBooks, getBooksByCategory } from '../data/booksData';

const Home = () => {
  const { isAuthenticated, user } = useAuth();
  const [animationTrigger, setAnimationTrigger] = useState(false);
  const [currentStats, setCurrentStats] = useState({ books: 0, readers: 0, rating: 0 });

  useEffect(() => {
    setAnimationTrigger(true);
    // Animate statistics
    const animateStats = () => {
      const targetStats = { books: 10000, readers: 50000, rating: 4.8 };
      const duration = 2000;
      const steps = 60;
      const stepDuration = duration / steps;

      let currentStep = 0;
      const interval = setInterval(() => {
        currentStep++;
        const progress = currentStep / steps;

        setCurrentStats({
          books: Math.floor(targetStats.books * progress),
          readers: Math.floor(targetStats.readers * progress),
          rating: (targetStats.rating * progress).toFixed(1)
        });

        if (currentStep >= steps) {
          clearInterval(interval);
        }
      }, stepDuration);
    };

    setTimeout(animateStats, 500);
  }, []);

  const categories = [
    {
      name: 'Fiction',
      description: 'Immerse yourself in captivating stories',
      icon: <AutoStories />,
      color: '#1976d2',
      books: getBooksByCategory('fiction').slice(0, 3)
    },
    {
      name: 'Non-Fiction',
      description: 'Expand your knowledge and understanding',
      icon: <TrendingUp />,
      color: '#388e3c',
      books: getBooksByCategory('non-fiction').slice(0, 3)
    },
    {
      name: 'Comics',
      description: 'Visual storytelling at its finest',
      icon: <Star />,
      color: '#f57c00',
      books: getBooksByCategory('comics').slice(0, 3)
    },
    {
      name: 'Feel-Good',
      description: 'Uplifting stories to brighten your day',
      icon: <Favorite />,
      color: '#e91e63',
      books: getBooksByCategory('feel-good').slice(0, 3)
    }
  ];

  const testimonials = [
    {
      name: "Sarah Johnson",
      text: "Amazing selection and fast delivery! Found books I couldn't find anywhere else.",
      rating: 5,
      avatar: "https://via.placeholder.com/60?text=SJ"
    },
    {
      name: "Michael Chen",
      text: "The recommendations are spot-on. Discovered my new favorite author here!",
      rating: 5,
      avatar: "https://via.placeholder.com/60?text=MC"
    },
    {
      name: "Emily Rodriguez",
      text: "Great prices and excellent customer service. Highly recommended!",
      rating: 5,
      avatar: "https://via.placeholder.com/60?text=ER"
    }
  ];

  return (
    <Container maxWidth="lg">
      {/* Hero Section */}
      <Box
        sx={{
          bgcolor: 'primary.main',
          color: 'white',
          py: 8,
          px: 4,
          borderRadius: 2,
          textAlign: 'center',
          mb: 6,
          mt: 4
        }}
      >
        <Typography variant="h2" component="h1" gutterBottom>
          Welcome to Our Bookstore
          {isAuthenticated && user && (
            <Typography variant="h4" component="span" sx={{ display: 'block', mt: 2 }}>
              Hello, {user.firstName}!
            </Typography>
          )}
        </Typography>
        <Typography variant="h5" paragraph>
          Discover amazing books and expand your knowledge
        </Typography>
        <Button
          variant="contained"
          color="secondary"
          size="large"
          component={Link}
          to="/products"
          sx={{ mt: 2 }}
        >
          Browse Books
        </Button>
      </Box>

      {/* Featured Books Section */}
      <Box sx={{ mb: 6 }}>
        <Typography variant="h4" component="h2" gutterBottom align="center">
          Featured Books
        </Typography>
        <Grid container spacing={4} sx={{ mt: 2 }}>
          {featuredBooks.map((book) => (
            <Grid item key={book.id} xs={12} sm={6} md={4}>
              <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
                <CardMedia
                  component="img"
                  height="300"
                  image={book.image}
                  alt={book.title}
                />
                <CardContent sx={{ flexGrow: 1 }}>
                  <Typography gutterBottom variant="h6" component="h3">
                    {book.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    by {book.author}
                  </Typography>
                  <Typography variant="h6" color="primary" sx={{ mt: 1 }}>
                    â‚¹{book.price}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button 
                    size="small" 
                    component={Link} 
                    to={`/products/${book.id}`}
                  >
                    View Details
                  </Button>
                  {isAuthenticated && (
                    <Button size="small" color="primary">
                      Add to Cart
                    </Button>
                  )}
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Box>

      {/* Features Section */}
      <Box sx={{ py: 6, bgcolor: 'grey.100', borderRadius: 2, px: 4 }}>
        <Typography variant="h4" component="h2" gutterBottom align="center">
          Why Choose Our Bookstore?
        </Typography>
        <Grid container spacing={4} sx={{ mt: 2 }}>
          <Grid item xs={12} md={4}>
            <Box textAlign="center">
              <Typography variant="h6" gutterBottom>
                Wide Selection
              </Typography>
              <Typography variant="body1">
                Thousands of books across all genres and categories
              </Typography>
            </Box>
          </Grid>
          <Grid item xs={12} md={4}>
            <Box textAlign="center">
              <Typography variant="h6" gutterBottom>
                Fast Delivery
              </Typography>
              <Typography variant="body1">
                Quick and reliable delivery to your doorstep
              </Typography>
            </Box>
          </Grid>
          <Grid item xs={12} md={4}>
            <Box textAlign="center">
              <Typography variant="h6" gutterBottom>
                Best Prices
              </Typography>
              <Typography variant="body1">
                Competitive prices and regular discounts
              </Typography>
            </Box>
          </Grid>
        </Grid>
      </Box>
    </Container>
  );
};

export default Home;
