import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Box,
  Button,
  Grid,
  Card,
  CardContent,
  CardMedia,
  CardActions,
  Chip,
  Fade,
  Slide,
  Grow,
  Paper,
  Avatar
} from '@mui/material';
import {
  AutoStories,
  TrendingUp,
  LocalShipping,
  Star,
  People,
  Favorite,
  ArrowForward
} from '@mui/icons-material';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import BookCarousel from '../components/BookCarousel';
import { featuredBooks, getBooksByCategory } from '../data/booksData';

const Home = () => {
  const { isAuthenticated, user } = useAuth();
  const [animationTrigger, setAnimationTrigger] = useState(false);
  const [currentStats, setCurrentStats] = useState({ books: 0, readers: 0, rating: 0 });

  useEffect(() => {
    setAnimationTrigger(true);
    // Animate statistics
    const animateStats = () => {
      const targetStats = { books: 10000, readers: 50000, rating: 4.8 };
      const duration = 2000;
      const steps = 60;
      const stepDuration = duration / steps;

      let currentStep = 0;
      const interval = setInterval(() => {
        currentStep++;
        const progress = currentStep / steps;

        setCurrentStats({
          books: Math.floor(targetStats.books * progress),
          readers: Math.floor(targetStats.readers * progress),
          rating: (targetStats.rating * progress).toFixed(1)
        });

        if (currentStep >= steps) {
          clearInterval(interval);
        }
      }, stepDuration);
    };

    setTimeout(animateStats, 500);
  }, []);

  const categories = [
    {
      name: 'Fiction',
      description: 'Immerse yourself in captivating stories',
      icon: <AutoStories />,
      color: '#1976d2',
      books: getBooksByCategory('fiction').slice(0, 3)
    },
    {
      name: 'Non-Fiction',
      description: 'Expand your knowledge and understanding',
      icon: <TrendingUp />,
      color: '#388e3c',
      books: getBooksByCategory('non-fiction').slice(0, 3)
    },
    {
      name: 'Comics',
      description: 'Visual storytelling at its finest',
      icon: <Star />,
      color: '#f57c00',
      books: getBooksByCategory('comics').slice(0, 3)
    },
    {
      name: 'Feel-Good',
      description: 'Uplifting stories to brighten your day',
      icon: <Favorite />,
      color: '#e91e63',
      books: getBooksByCategory('feel-good').slice(0, 3)
    }
  ];

  const testimonials = [
    {
      name: "Sarah Johnson",
      text: "Amazing selection and fast delivery! Found books I couldn't find anywhere else.",
      rating: 5,
      avatar: "https://via.placeholder.com/60?text=SJ"
    },
    {
      name: "Michael Chen",
      text: "The recommendations are spot-on. Discovered my new favorite author here!",
      rating: 5,
      avatar: "https://via.placeholder.com/60?text=MC"
    },
    {
      name: "Emily Rodriguez",
      text: "Great prices and excellent customer service. Highly recommended!",
      rating: 5,
      avatar: "https://via.placeholder.com/60?text=ER"
    }
  ];

  return (
    <Box>
      {/* Hero Section with Gradient Background */}
      <Box
        sx={{
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          color: 'white',
          py: { xs: 8, md: 12 },
          position: 'relative',
          overflow: 'hidden'
        }}
      >
        {/* Animated Background Elements */}
        <Box
          sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            opacity: 0.1,
            backgroundImage: 'url("data:image/svg+xml,%3Csvg width="60" height="60" viewBox="0 0 60 60" xmlns="http://www.w3.org/2000/svg"%3E%3Cg fill="none" fill-rule="evenodd"%3E%3Cg fill="%23ffffff" fill-opacity="0.1"%3E%3Ccircle cx="30" cy="30" r="4"/%3E%3C/g%3E%3C/g%3E%3C/svg%3E")',
            animation: 'float 6s ease-in-out infinite'
          }}
        />

        <Container maxWidth="lg">
          <Fade in={animationTrigger} timeout={1000}>
            <Box sx={{ textAlign: 'center', position: 'relative', zIndex: 1 }}>
              <Slide direction="down" in={animationTrigger} timeout={800}>
                <Typography 
                  variant="h1" 
                  component="h1" 
                  gutterBottom
                  sx={{ 
                    fontWeight: 'bold',
                    fontSize: { xs: '2.5rem', md: '4rem' },
                    textShadow: '2px 2px 4px rgba(0,0,0,0.3)'
                  }}
                >
                  Discover Your Next
                  <Typography 
                    component="span" 
                    sx={{ 
                      display: 'block',
                      background: 'linear-gradient(45deg, #FFD700, #FFA500)',
                      WebkitBackgroundClip: 'text',
                      WebkitTextFillColor: 'transparent',
                      backgroundClip: 'text'
                    }}
                  >
                    Great Read
                  </Typography>
                </Typography>
              </Slide>

              {isAuthenticated && user && (
                <Grow in={animationTrigger} timeout={1200}>
                  <Typography variant="h4" sx={{ mb: 2, opacity: 0.9 }}>
                    Welcome back, {user.firstName}! 📚
                  </Typography>
                </Grow>
              )}

              <Slide direction="up" in={animationTrigger} timeout={1000}>
                <Typography 
                  variant="h5" 
                  paragraph 
                  sx={{ 
                    maxWidth: 600, 
                    mx: 'auto', 
                    mb: 4,
                    opacity: 0.95
                  }}
                >
                  Explore our curated collection of books across all genres. 
                  From bestsellers to hidden gems, find your perfect story.
                </Typography>
              </Slide>

              <Grow in={animationTrigger} timeout={1400}>
                <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', flexWrap: 'wrap' }}>
                  <Button
                    variant="contained"
                    size="large"
                    component={Link}
                    to="/products"
                    endIcon={<ArrowForward />}
                    sx={{
                      bgcolor: 'rgba(255,255,255,0.2)',
                      backdropFilter: 'blur(10px)',
                      border: '1px solid rgba(255,255,255,0.3)',
                      '&:hover': {
                        bgcolor: 'rgba(255,255,255,0.3)',
                        transform: 'translateY(-2px)'
                      },
                      transition: 'all 0.3s ease'
                    }}
                  >
                    Explore Books
                  </Button>
                  {!isAuthenticated && (
                    <Button
                      variant="outlined"
                      size="large"
                      component={Link}
                      to="/signup"
                      sx={{
                        borderColor: 'rgba(255,255,255,0.5)',
                        color: 'white',
                        '&:hover': {
                          borderColor: 'white',
                          bgcolor: 'rgba(255,255,255,0.1)'
                        }
                      }}
                    >
                      Join Us
                    </Button>
                  )}
                </Box>
              </Grow>
            </Box>
          </Fade>
        </Container>
      </Box>

      {/* Statistics Section */}
      <Container maxWidth="lg" sx={{ py: 6 }}>
        <Fade in={animationTrigger} timeout={1500}>
          <Grid container spacing={4} sx={{ textAlign: 'center' }}>
            <Grid item xs={12} md={4}>
              <Paper elevation={3} sx={{ p: 4, borderRadius: 3 }}>
                <AutoStories sx={{ fontSize: 48, color: 'primary.main', mb: 2 }} />
                <Typography variant="h3" color="primary" sx={{ fontWeight: 'bold' }}>
                  {currentStats.books.toLocaleString()}+
                </Typography>
                <Typography variant="h6" color="text.secondary">
                  Books Available
                </Typography>
              </Paper>
            </Grid>
            <Grid item xs={12} md={4}>
              <Paper elevation={3} sx={{ p: 4, borderRadius: 3 }}>
                <People sx={{ fontSize: 48, color: 'secondary.main', mb: 2 }} />
                <Typography variant="h3" color="secondary" sx={{ fontWeight: 'bold' }}>
                  {currentStats.readers.toLocaleString()}+
                </Typography>
                <Typography variant="h6" color="text.secondary">
                  Happy Readers
                </Typography>
              </Paper>
            </Grid>
            <Grid item xs={12} md={4}>
              <Paper elevation={3} sx={{ p: 4, borderRadius: 3 }}>
                <Star sx={{ fontSize: 48, color: 'warning.main', mb: 2 }} />
                <Typography variant="h3" color="warning.main" sx={{ fontWeight: 'bold' }}>
                  {currentStats.rating}★
                </Typography>
                <Typography variant="h6" color="text.secondary">
                  Average Rating
                </Typography>
              </Paper>
            </Grid>
          </Grid>
        </Fade>
      </Container>

      {/* Featured Books Carousel */}
      <Container maxWidth="lg">
        <Fade in={animationTrigger} timeout={2000}>
          <BookCarousel books={featuredBooks} title="Featured Books" />
        </Fade>
      </Container>

      {/* Categories Section */}
      <Box sx={{ bgcolor: 'grey.50', py: 8 }}>
        <Container maxWidth="lg">
          <Fade in={animationTrigger} timeout={2200}>
            <Typography 
              variant="h3" 
              component="h2" 
              gutterBottom 
              align="center"
              sx={{ mb: 6, fontWeight: 'bold' }}
            >
              Explore by Category
            </Typography>
          </Fade>

          <Grid container spacing={4}>
            {categories.map((category, index) => (
              <Grid item xs={12} sm={6} md={3} key={category.name}>
                <Grow in={animationTrigger} timeout={1000 + index * 200}>
                  <Card 
                    sx={{ 
                      height: '100%',
                      textAlign: 'center',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        transform: 'translateY(-8px)',
                        boxShadow: 6
                      }
                    }}
                    component={Link}
                    to={`/products?category=${category.name.toLowerCase()}`}
                  >
                    <CardContent sx={{ p: 4 }}>
                      <Box 
                        sx={{ 
                          width: 80, 
                          height: 80, 
                          borderRadius: '50%', 
                          bgcolor: category.color,
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          mx: 'auto',
                          mb: 3,
                          color: 'white'
                        }}
                      >
                        {React.cloneElement(category.icon, { sx: { fontSize: 40 } })}
                      </Box>
                      <Typography variant="h5" gutterBottom sx={{ fontWeight: 'bold' }}>
                        {category.name}
                      </Typography>
                      <Typography variant="body1" color="text.secondary" paragraph>
                        {category.description}
                      </Typography>
                      <Typography variant="body2" color="primary">
                        {category.books.length}+ books available
                      </Typography>
                    </CardContent>
                  </Card>
                </Grow>
              </Grid>
            ))}
          </Grid>
        </Container>
      </Box>

      {/* Testimonials Section */}
      <Container maxWidth="lg" sx={{ py: 8 }}>
        <Fade in={animationTrigger} timeout={2500}>
          <Typography 
            variant="h3" 
            component="h2" 
            gutterBottom 
            align="center"
            sx={{ mb: 6, fontWeight: 'bold' }}
          >
            What Our Readers Say
          </Typography>
        </Fade>

        <Grid container spacing={4}>
          {testimonials.map((testimonial, index) => (
            <Grid item xs={12} md={4} key={testimonial.name}>
              <Slide direction="up" in={animationTrigger} timeout={1500 + index * 300}>
                <Paper 
                  elevation={2} 
                  sx={{ 
                    p: 4, 
                    height: '100%',
                    borderRadius: 3,
                    transition: 'all 0.3s ease',
                    '&:hover': {
                      boxShadow: 4
                    }
                  }}
                >
                  <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                    <Avatar src={testimonial.avatar} sx={{ mr: 2 }} />
                    <Box>
                      <Typography variant="h6">{testimonial.name}</Typography>
                      <Box sx={{ display: 'flex' }}>
                        {[...Array(testimonial.rating)].map((_, i) => (
                          <Star key={i} sx={{ color: 'gold', fontSize: 16 }} />
                        ))}
                      </Box>
                    </Box>
                  </Box>
                  <Typography variant="body1" sx={{ fontStyle: 'italic' }}>
                    "{testimonial.text}"
                  </Typography>
                </Paper>
              </Slide>
            </Grid>
          ))}
        </Grid>
      </Container>

      {/* Call to Action */}
      <Box 
        sx={{ 
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          color: 'white',
          py: 8,
          textAlign: 'center'
        }}
      >
        <Container maxWidth="md">
          <Fade in={animationTrigger} timeout={3000}>
            <Typography variant="h3" gutterBottom sx={{ fontWeight: 'bold' }}>
              Ready to Start Reading?
            </Typography>
            <Typography variant="h6" paragraph sx={{ opacity: 0.9 }}>
              Join thousands of book lovers and discover your next favorite story today.
            </Typography>
            <Button
              variant="contained"
              size="large"
              component={Link}
              to={isAuthenticated ? "/products" : "/signup"}
              sx={{
                bgcolor: 'rgba(255,255,255,0.2)',
                backdropFilter: 'blur(10px)',
                border: '1px solid rgba(255,255,255,0.3)',
                '&:hover': {
                  bgcolor: 'rgba(255,255,255,0.3)',
                  transform: 'translateY(-2px)'
                },
                transition: 'all 0.3s ease'
              }}
            >
              {isAuthenticated ? "Browse Books" : "Get Started"}
            </Button>
          </Fade>
        </Container>
      </Box>

      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px); }
          50% { transform: translateY(-20px); }
        }
      `}</style>
    </Box>
  );
};

export default Home;
