import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  CardActions,
  Button,
  TextField,
  Box,
  CircularProgress,
  Alert,
  Pagination,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  Paper,
  Slider,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Checkbox,
  FormControlLabel
} from '@mui/material';
import {
  Search,
  FilterList,
  ExpandMore,
  Star,
  ShoppingCart
} from '@mui/icons-material';
import { Link, useSearchParams } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { booksData, searchBooks } from '../data/booksData';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [page, setPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const { isAuthenticated } = useAuth();

  useEffect(() => {
    fetchProducts();
  }, [page]);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      // Since the backend expects specific parameters, let's provide defaults
      const response = await axios.get('/api/products/products', {
        params: {
          category: 'all',
          pageNumber: page - 1, // Backend uses 0-based indexing
          pageSize: 12
        }
      });

      if (response.data.content) {
        setProducts(response.data.content);
        setTotalPages(response.data.totalPages || 1);
      } else {
        // Fallback to mock data if API doesn't return expected format
        setProducts(getMockProducts());
      }
    } catch (err) {
      console.error('Error fetching products:', err);
      // Use mock data as fallback
      setProducts(getMockProducts());
      setError('');
    } finally {
      setLoading(false);
    }
  };

  const getMockProducts = () => {
    return [
      {
        id: 1,
        title: "The Great Gatsby",
        author: "F. Scott Fitzgerald",
        price: 299,
        discountedPrice: 249,
        imageUrl: "https://via.placeholder.com/200x300?text=Book+1",
        description: "A classic American novel"
      },
      {
        id: 2,
        title: "To Kill a Mockingbird",
        author: "Harper Lee",
        price: 399,
        discountedPrice: 349,
        imageUrl: "https://via.placeholder.com/200x300?text=Book+2",
        description: "A gripping tale of racial injustice"
      },
      {
        id: 3,
        title: "1984",
        author: "George Orwell",
        price: 349,
        discountedPrice: 299,
        imageUrl: "https://via.placeholder.com/200x300?text=Book+3",
        description: "A dystopian social science fiction novel"
      },
      {
        id: 4,
        title: "Pride and Prejudice",
        author: "Jane Austen",
        price: 279,
        discountedPrice: 229,
        imageUrl: "https://via.placeholder.com/200x300?text=Book+4",
        description: "A romantic novel of manners"
      },
      {
        id: 5,
        title: "The Catcher in the Rye",
        author: "J.D. Salinger",
        price: 329,
        discountedPrice: 279,
        imageUrl: "https://via.placeholder.com/200x300?text=Book+5",
        description: "A controversial coming-of-age story"
      },
      {
        id: 6,
        title: "Lord of the Flies",
        author: "William Golding",
        price: 299,
        discountedPrice: 249,
        imageUrl: "https://via.placeholder.com/200x300?text=Book+6",
        description: "A novel about the dark side of human nature"
      }
    ];
  };

  const addToCart = async (productId) => {
    try {
      await axios.put('/api/cart/add', {
        productId: productId,
        quantity: 1
      });
      alert('Product added to cart!');
    } catch (err) {
      console.error('Error adding to cart:', err);
      alert('Failed to add product to cart');
    }
  };

  const filteredProducts = products.filter(product =>
    product.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    product.author?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handlePageChange = (event, value) => {
    setPage(value);
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="50vh">
        <CircularProgress />
      </Box>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom align="center">
        Our Book Collection
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      {/* Search Bar */}
      <Box sx={{ mb: 4 }}>
        <TextField
          fullWidth
          label="Search books by title or author"
          variant="outlined"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </Box>

      {/* Products Grid */}
      <Grid container spacing={4}>
        {filteredProducts.map((product) => (
          <Grid item key={product.id} xs={12} sm={6} md={4}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
              <CardMedia
                component="img"
                height="300"
                image={product.imageUrl || `https://via.placeholder.com/200x300?text=${product.title}`}
                alt={product.title}
              />
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography gutterBottom variant="h6" component="h3">
                  {product.title}
                </Typography>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  by {product.author}
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                  {product.description}
                </Typography>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  {product.discountedPrice && product.discountedPrice < product.price ? (
                    <>
                      <Typography variant="h6" color="primary">
                        ₹{product.discountedPrice}
                      </Typography>
                      <Typography 
                        variant="body2" 
                        sx={{ textDecoration: 'line-through', color: 'text.secondary' }}
                      >
                        ₹{product.price}
                      </Typography>
                    </>
                  ) : (
                    <Typography variant="h6" color="primary">
                      ₹{product.price}
                    </Typography>
                  )}
                </Box>
              </CardContent>
              <CardActions>
                <Button 
                  size="small" 
                  component={Link} 
                  to={`/products/${product.id}`}
                >
                  View Details
                </Button>
                {isAuthenticated && (
                  <Button 
                    size="small" 
                    color="primary"
                    onClick={() => addToCart(product.id)}
                  >
                    Add to Cart
                  </Button>
                )}
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      {filteredProducts.length === 0 && (
        <Box textAlign="center" sx={{ mt: 4 }}>
          <Typography variant="h6" color="text.secondary">
            No products found matching your search.
          </Typography>
        </Box>
      )}

      {/* Pagination */}
      {totalPages > 1 && (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <Pagination 
            count={totalPages} 
            page={page} 
            onChange={handlePageChange} 
            color="primary" 
          />
        </Box>
      )}
    </Container>
  );
};

export default Products;
