import React, { useState } from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardMedia,
  Button,
  IconButton,
  Box,
  Divider,
  TextField,
  CircularProgress,
  Alert,
  Paper
} from '@mui/material';
import {
  Add,
  Remove,
  Delete,
  ShoppingCartCheckout
} from '@mui/icons-material';
import { useAuth } from '../context/AuthContext';
import { useCart } from '../context/CartContext';

const Cart = () => {
  const [updating, setUpdating] = useState(false);
  const { user, isAuthenticated } = useAuth();
  const { 
    cartItems, 
    loading, 
    removeFromCart, 
    updateQuantity, 
    clearCart, 
    getCartTotal 
  } = useCart();

  if (!isAuthenticated) {
    return (
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <Paper sx={{ p: 4, textAlign: 'center' }}>
          <Typography variant="h5" gutterBottom>
            Please login to view your cart
          </Typography>
          <Button variant="contained" href="/login">
            Login
          </Button>
        </Paper>
      </Container>
    );
  }

  const getMockCart = () => {
    return {
      id: 1,
      cartitems: [
        {
          id: 1,
          product: {
            id: 1,
            title: "The Great Gatsby",
            author: "F. Scott Fitzgerald",
            price: 299,
            discountedPrice: 249,
            imageUrl: "https://via.placeholder.com/150x200?text=Book+1"
          },
          quantity: 2,
          price: 498,
          discountedPrice: 498
        },
        {
          id: 2,
          product: {
            id: 2,
            title: "To Kill a Mockingbird",
            author: "Harper Lee",
            price: 399,
            discountedPrice: 349,
            imageUrl: "https://via.placeholder.com/150x200?text=Book+2"
          },
          quantity: 1,
          price: 399,
          discountedPrice: 349
        }
      ],
      totalPrice: 897,
      totalDiscountedPrice: 847,
      totalItem: 3,
      discount: 50
    };
  };

  const updateCartItemQuantity = async (cartItemId, newQuantity) => {
    if (newQuantity < 1) return;

    setUpdating(true);
    try {
      const cartItem = cart.cartitems.find(item => item.id === cartItemId);
      await axios.put(`/api/cart_items/${cartItemId}`, {
        ...cartItem,
        quantity: newQuantity
      });
      fetchCart();
    } catch (err) {
      console.error('Error updating cart item:', err);
      setError('Failed to update cart item');
    } finally {
      setUpdating(false);
    }
  };

  const removeCartItem = async (cartItemId) => {
    setUpdating(true);
    try {
      await axios.delete(`/api/cart_items/${cartItemId}`);
      fetchCart();
    } catch (err) {
      console.error('Error removing cart item:', err);
      setError('Failed to remove cart item');
    } finally {
      setUpdating(false);
    }
  };

  const handleCheckout = () => {
    // In a real application, this would navigate to a checkout page
    alert('Checkout functionality would be implemented here!');
  };

  if (loading) {
    return (
      <Box display="flex" justifyContent="center" alignItems="center" minHeight="50vh">
        <CircularProgress />
      </Box>
    );
  }

  if (!cart || !cart.cartitems || cart.cartitems.length === 0) {
    return (
      <Container maxWidth="md" sx={{ py: 4 }}>
        <Paper sx={{ p: 4, textAlign: 'center' }}>
          <Typography variant="h5" gutterBottom>
            Your cart is empty
          </Typography>
          <Typography variant="body1" color="text.secondary" paragraph>
            Add some books to your cart to get started!
          </Typography>
          <Button variant="contained" href="/products">
            Browse Books
          </Button>
        </Paper>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom>
        Shopping Cart
      </Typography>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }}>
          {error}
        </Alert>
      )}

      <Grid container spacing={4}>
        {/* Cart Items */}
        <Grid item xs={12} md={8}>
          {cart.cartitems.map((item) => (
            <Card key={item.id} sx={{ mb: 2 }}>
              <CardContent>
                <Grid container spacing={2} alignItems="center">
                  {/* Product Image */}
                  <Grid item xs={12} sm={3}>
                    <CardMedia
                      component="img"
                      height="150"
                      image={item.product.imageUrl || `https://via.placeholder.com/150x200?text=${item.product.title}`}
                      alt={item.product.title}
                      sx={{ objectFit: 'cover', borderRadius: 1 }}
                    />
                  </Grid>

                  {/* Product Details */}
                  <Grid item xs={12} sm={6}>
                    <Typography variant="h6" gutterBottom>
                      {item.product.title}
                    </Typography>
                    <Typography variant="body2" color="text.secondary" gutterBottom>
                      by {item.product.author}
                    </Typography>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
                      {item.product.discountedPrice && item.product.discountedPrice < item.product.price ? (
                        <>
                          <Typography variant="h6" color="primary">
                            ₹{item.product.discountedPrice}
                          </Typography>
                          <Typography 
                            variant="body2" 
                            sx={{ textDecoration: 'line-through', color: 'text.secondary' }}
                          >
                            ₹{item.product.price}
                          </Typography>
                        </>
                      ) : (
                        <Typography variant="h6" color="primary">
                          ₹{item.product.price}
                        </Typography>
                      )}
                    </Box>

                    {/* Quantity Controls */}
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <IconButton 
                        onClick={() => updateCartItemQuantity(item.id, item.quantity - 1)}
                        disabled={updating || item.quantity <= 1}
                        size="small"
                      >
                        <Remove />
                      </IconButton>
                      <TextField
                        size="small"
                        value={item.quantity}
                        onChange={(e) => {
                          const newQuantity = parseInt(e.target.value) || 1;
                          updateCartItemQuantity(item.id, newQuantity);
                        }}
                        inputProps={{ 
                          min: 1, 
                          style: { textAlign: 'center', width: '60px' } 
                        }}
                      />
                      <IconButton 
                        onClick={() => updateCartItemQuantity(item.id, item.quantity + 1)}
                        disabled={updating}
                        size="small"
                      >
                        <Add />
                      </IconButton>
                    </Box>
                  </Grid>

                  {/* Price and Actions */}
                  <Grid item xs={12} sm={3}>
                    <Box sx={{ textAlign: 'right' }}>
                      <Typography variant="h6" gutterBottom>
                        ₹{item.discountedPrice || item.price}
                      </Typography>
                      <IconButton 
                        onClick={() => removeCartItem(item.id)}
                        disabled={updating}
                        color="error"
                      >
                        <Delete />
                      </IconButton>
                    </Box>
                  </Grid>
                </Grid>
              </CardContent>
            </Card>
          ))}
        </Grid>

        {/* Order Summary */}
        <Grid item xs={12} md={4}>
          <Card sx={{ position: 'sticky', top: 20 }}>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Order Summary
              </Typography>
              <Divider sx={{ mb: 2 }} />

              <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                <Typography variant="body1">
                  Items ({cart.totalItem})
                </Typography>
                <Typography variant="body1">
                  ₹{cart.totalPrice}
                </Typography>
              </Box>

              {cart.discount > 0 && (
                <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 1 }}>
                  <Typography variant="body1" color="success.main">
                    Discount
                  </Typography>
                  <Typography variant="body1" color="success.main">
                    -₹{cart.discount}
                  </Typography>
                </Box>
              )}

              <Divider sx={{ my: 2 }} />

              <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
                <Typography variant="h6">
                  Total
                </Typography>
                <Typography variant="h6" color="primary">
                  ₹{cart.totalDiscountedPrice || cart.totalPrice}
                </Typography>
              </Box>

              <Button
                variant="contained"
                fullWidth
                size="large"
                startIcon={<ShoppingCartCheckout />}
                onClick={handleCheckout}
                disabled={updating}
              >
                Proceed to Checkout
              </Button>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
};

export default Cart;
